                                                                                                                                                                                                                                                                                                                                                                                        fi#!/bin/bash

# Simple way to use raspberry pi through a headless wireless interface
# You have to place it on your crontab


adaptador=$(ifconfig -a | grep -B 1 "aa:aa:aa:aa:aa:aa" | head -n1  | awk '{print$1}' | sed 's,:,,g')

if iwconfig $adaptador | grep -iE "essid1|essid2";then

        echo "Already connected"
else


        if [ -f /var/run/wpa_supplicant/$adaptador ];then

                rm -rf /var/run/wpa_supplicant/$adaptador
                wpa_supplicant -c /etc/wpa_supplicant/wpa_supplicant.custom.conf -i $adaptador


        else
                rm -rf /var/run/wpa_supplicant/$adaptador
                wpa_supplicant -c /etc/wpa_supplicant/wpa_supplicant.custom.conf -i $adaptador


        fi


fi                                                                                                                                                                              fi
